import React, { useState } from 'react';
import { View, Text, Button, Alert } from 'react-native';
import styles from './styles';

function Landing({ beauty }) {
    const [name, setName] = useState('panteras');
    const [secret, setSecret] = useState(false);

    function toogleSecret() {
        setSecret(!secret);
    }

    function handleAlert() {
        Alert.alert('WARNING!', 'Pau guapo', [
            {
                text: "No, fuck you",
                onPress: () => Alert.alert("Wow, you're rude :(")
            },
            {
                text: "I agree, Pau guapo",
                onPress: () => Alert.alert(":DDDDDD")
            }
        ],
            { cancelable: false })
    }

    return (<>
        <View>
            <Text onPress={() => toogleSecret()} style={[styles.bold, styles.padding, styles.margin]}>Hola {beauty} {name}!!!!!!</Text>
            <Button title="Click me!" onPress={() => handleAlert()} />
            {secret && <Text>Sergi Ramos nunca actualiz√≥ el Trello</Text>}
        </View>
    </>)
}

export default Landing;
