import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    stickyMessage: {
        fontSize: 20,
        marginVertical: 15
    },
    stickyAuthor: {
        fontWeight: 'bold'
    },
    stickyDate: {
        fontStyle: 'italic'
    },
    separator: {
        height: 2,
        backgroundColor: 'black',
        marginVertical: 10
    }
})

export default styles;
