import { StyleSheet, Dimensions } from 'react-native';

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
    },
    header: {
        fontWeight: 'bold',
        fontSize: 45,
        textAlign: 'center',
        color: '#e9c46a',
        borderColor: 'plum',
        borderWidth: 5
    },
    boxContainer: {
        borderRadius: 50,
        marginTop: 50,
        padding: 45,
        borderWidth: 2,
        borderColor: 'grey',
        backgroundColor: '#2a9d8f'
    },
    stickiesContainer: {
        width: '90%'
    },
    stickyContainer: {
        backgroundColor: '#f4a261',
        marginVertical: 20,
        padding: 15,
        shadowColor: '#fff',
        shadowOffset: { width: 0, height: 1 },
        shadowOpacity: 0.2,
        shadowRadius: 1,
        elevation: 5
    },
    stickyMessage: {
        fontSize: 20,
        marginVertical: 15
    },
    stickyAuthor: {
        fontWeight: 'bold'
    },
    stickyDate: {
        fontStyle: 'italic'
    },
    separator: {
        height: 2,
        backgroundColor: 'black',
        marginVertical: 10
    }
});

export default styles;
